// here goes the API set up and the db set up
// import the express library
const express = require('express');
// app is all the exress stuff we get from the library (apply middleware telling the API to start, creating routes)
const app = express();
const mongoose = require('mongoose');
const UserModel = require('.models/Users');

app.use(express.json()); 

mongoose.connect(process.env.MONGO_URI);

// get and post requests (normally routs would used)
app.get("/getUsers", (req, res) => {
    // Just the find function retrieves all the users.
    UserModel.find({}, (err, result) => {
        if (err) {
            // If there's an error, making the request
            res.json(err);
        } else {
            res.json(result); 
        }
    });
});

app.post("/createUser", async (req, res) => {
    const user = req.body;
    const newUser = new UserModel(user);
    await newUser.save();
    res.json(user);
});

// tell the API to start
app.listen(3001, () => {
    console.log('Server runs on port 3001');
 }); 